function is_post($url){
	if(strpos($url,"story_fbid") > 1){
		if(strpos($url,"&id=") > 1){
			$url=explode("&id=",$url)[0];
		}
		$id=explode("fbid=",$url);
		$fbid=$id[1];
		return $fbid;
	}else{
		return 0;
	}
}
function is_photo($url){
	if(strpos($url,"?fbid") > 1){
		if(strpos($url,"&id=") > 1){
			$url=explode("&id=",$url)[0];
		}
		$id=explode("fbid=",$url);
		$fbid=$id[1];
		return $fbid;
	}else{
		return 0;
	}
}
